mapper.select:
select step_no, mission_no, step, step_title, step_content, step_time, mission_skill, userid
from t_mission_step
where step_no = ${param1}

mapper.update:
update t_mission_step set step=#{step}, step_title=#{step_title}, step_content=#{step_content},
step_time=#{step_time}, mission_skill=#{mission_skill}
where step_no=${step_no} and userid=#{userid}
